	{
		if(null === $this->collPicturess || null !== $criteria) {
			if ($this->isNew() && null === $this->collPicturess) {
				// return empty collection
				$this->initPicturess();
			} else {
				$collPicturess = PicturesQuery::create(null, $criteria)
					->filterByDestination($this)
					->find($con);
				if (null !== $criteria) {
