	public function countPicturess(Criteria $criteria = null, $distinct = false, PropelPDO $con = null)
	{
		if(null === $this->collPicturess || null !== $criteria) {
			if ($this->isNew() && null === $this->collPicturess) {
				return 0;
			} else {
				$query = PicturesQuery::create(null, $criteria);
				if($distinct) {
					$query->distinct();
				}
				return $query
					->filterByDestination($this)
					->count($con);
			}
		} else {
			return count($this->collPicturess);
		}
