	 *
	 * @param      Pictures $l Pictures
	 * @return     void
	 * @throws     PropelException
	 */
	public function addPictures(Pictures $l)
	{
		if ($this->collPicturess === null) {
			$this->initPicturess();
		}
		if (!$this->collPicturess->contains($l)) { // only add it if the **same** object is not already associated
